#!/usr/bin/env python
import public
import runcmd


def _run(args):
    return runcmd.run(["google-chrome"] + list(args))._raise().out


@public.add
def detect():
    """return True if fullscreen mode detected"""
    out = _run(["fullscreen-detect"])
    return "true" in out


@public.add
def enter():
    """enter fullscreen mode"""
    _run(["fullscreen-enter"])


@public.add
def exit():
    """exit fullscreen mode"""
    _run(["fullscreen-exit"])
